example.synopsis:
@version: 3.11
@module geoip2

options {
    keep_hostname(yes);
};

source s_file {
    file("/tmp/input");
};

parser p_geoip2 { geoip2( "${HOST}", prefix( "geoip2." ) database( "/usr/share/GeoIP2/GeoLiteCity.dat" ) ); };

destination d_file {
    file( "/tmp/output" template("$(format-json --scope core --key geoip2*)\n") );
};


log {
    source(s_file);
    parser(p_geoip2);
    destination(d_file);
};
